import React from "react";

import "./MovieCard.css";

import Rating from "@material-ui/lab/Rating";
import { Box } from "@material-ui/core";

const MovieCard = ({
  movie: { name, image, date, type, rating, description },
  handleMovie,
}) => {
  return (

    <div id="card_container" data-offset="2">
  <div class="pg">
    <img src={image}/>
  </div>
  <div id="card">
    <div class="shine"></div>
    <div class="text-block">
      <h1> {name} <small>{date}</small></h1>
      <li>||</li>
            <li>{type}</li>
      <p>
        Arthur Curry learns that he is the heir to the underwater kingdom of Atlantis, and must step forward to lead his people and be a hero to the world.
      </p>
      <button>Watch Trailer</button>
    </div>
       <div className="card_right__rating">
          <Box component="fieldset" mb={3} borderColor="transparent">
            <Rating name="read-only" value={rating} readOnly />
          </Box>
        </div>
        <div className="card_right__review">
          <p>{description}</p>
          <a href="https://www.imdb.com/?ref_=nv_home">Read More</a>
        </div>
        <div className="card_right__button">
          <a href="https://www.imdb.com/?ref_=nv_home">WATCH TRAILER</a>
        </div>  
  </div>
</div>


  );
};

export default MovieCard;
